% Butterworth Bandpass filter designed using FDESIGN.BANDPASS.

lo = 50;
hi = 7000;
n_filters = 8;

bands = logspace(log10(lo), log10(hi), n_filters + 1);

for i = 1:n_filters
    % All frequency values are in Hz.
    Fs = 48000;  % Sampling Frequency
    
    N   = 4;   % Order
    Fc1 = bands(i);  % First Cutoff Frequency
    Fc2 = bands(i+1);  % Second Cutoff Frequency
    
    % Construct an FDESIGN object and call its BUTTER method.
    h  = fdesign.bandpass('N,F3dB1,F3dB2', N, Fc1, Fc2, Fs);
    Hd = design(h, 'butter');
    
    % Set the arithmetic property.
    set(Hd, 'Arithmetic', 'fixed', ...
        'CoeffWordLength', 32, ...
        'CoeffAutoScale', true, ...
        'ProductMode', 'Fullprecision', ...
        'AccumMode', 'Fullprecision', ...
        'StateWordLength', 24, ...
        'StateFracLength', 23, ...
        'SectioninputWordLength', 58, ...
        'SectioninputAutoScale', true, ...
        'SectionoutputWordLength', 58, ...
        'SectionoutputAutoScale', true, ...
        'InputWordLength', 24, ...
        'inputFracLength', 23, ...
        'OutputWordLength', 24, ...
        'OutputMode', 'AvoidOverflow', ...
        'signed',        true, ...
        'RoundMode',     'convergent', ...
        'OverflowMode',  'Wrap', ...
        'CastBeforeSum', false);
    generatehdl(Hd, 'InputDataType', numerictype(1,24,23), 'Name', strcat('filter', num2str(i)),...
        'AddPipelineRegisters', 'on', 'FoldingFactor', 12);
end
